class BrowserFn{local(r){return{get:()=>JSON.parse(localStorage.getItem(r)??"[]"),set:e=>{localStorage.setItem(r,JSON.stringify(e))}}}}class DomFn{isElem(e,r=HTMLElement){return e instanceof r}select(e){return document.querySelector(e)}selectAll(e){return Array.from(document.querySelectorAll(e))}create(e,r){var t=document.createElement(e);if(r)for(var[n,l]of Object.entries(r))t.setAttribute(n,l);return t}removeChildren(e){if(!this.isElem(e))throw new Error(`Invalid parent: ${e}.`);for(;e.firstElementChild;)e.firstElementChild.remove()}prependHtml(e,r){if(!this.isElem(e))throw new Error(`Invalid parent: ${e}.`);e.insertAdjacentHTML("afterbegin",r)}appendHtml(e,r){if(!this.isElem(e))throw new Error(`Invalid parent: ${e}.`);e.insertAdjacentHTML("beforeend",r)}}export{BrowserFn,DomFn};