class NumberFn{range(t=100,e=0,r=1,n=0){for(var a=[];e<=t;)a.push(e),e+=r;var o=a.join(" ");return[a,o][n]}rand(t=100,e=0){return Math.floor(Math.random()*(t-e+1))+e}avg(t){return t.length?t.reduce((t,e)=>t+e)/t.length:0}median(t){var e,t=t.toSorted((t,e)=>t-e);return 1&t.length?t[Math.floor(t.length/2)]:(e=t.length/2,this.avg([t[e-1],t[e]]))}clamp(t,e,r){return Math.min(Math.max(t,e),r)}dist(t,e){return Math.hypot(t[0]-e[0],t[1]-e[1])}destPosition(t,e,r){e=this.degToRad(e);return[t[0]+Math.cos(e)*r,t[1]+Math.sin(e)*r]}diff(t,e){return Math.abs(t-e)}radToDeg(t){return t*(180/Math.PI)}degToRad(t){return t*(Math.PI/180)}loop(t,e=0,r=100){return t=r<(t=t<e?r:t)?e:t}formatTwoDigit(t){return t<10?"0"+t:t}areEqualPoints(e,...t){return t.every(t=>t[0]===e[0]&&t[1]===e[1])}isBetween(t,e,r,n=!0){return n?e<t&&t<r:e<=t&&t<=r}}class StringFn{strRev(t){return t.split("").reverse().join("")}sanitize(t,e=100){return!t||"string"!=typeof t||!(t=t.trim())||t.length>e?null:t}}class DateFn{constructor(){this.numFn=new NumberFn}dateToUnix(t){return Math.round(Date.parse(t)/1e3)}unixToDate(t){return new Date(1e3*t)}daysToSeconds(t=7){return 86400*t}secondsToDays(t){return Math.round(t/86400)}today(){var t=new Date;return this.numFn.formatTwoDigit(t.getDate())+`/${this.numFn.formatTwoDigit(t.getMonth()+1)}/`+t.getFullYear()}time(){var t=new Date;return this.numFn.formatTwoDigit(t.getHours())+`:${this.numFn.formatTwoDigit(t.getMinutes())}:`+this.numFn.formatTwoDigit(t.getSeconds())}datetime(){return this.today()+" "+this.time()}}class FetchFn{async#returnData(t,e){let r;switch(e){case"json":r=await t.json();break;case"text":r=await t.text()}return r}async get(t=null,e="json",r=location.href){t=this.objToHttpReq(t),r=await fetch(""+r+t);return this.#returnData(r,e)}async post(t=null,e="json",r=location.href){t=this.objToHttpReq(t),r=await fetch(r,{method:"post",body:t.substring(1),headers:{"content-type":"application/x-www-form-urlencoded"}});return this.#returnData(r,e)}objToHttpReq(t=null){let e="?";if(t)for(var r in t){var n=(""+t[r]).trim();e+=`${encodeURIComponent(r)}=${encodeURIComponent(n)}&`}return e}httpReqToObj(t){var r={};let e=t.includes("?")?t.split("?"):t;var n=("string"==typeof e?e:e[1]).split("&");for(let e=0;e<n.length;e++){let t=n[e].split("=");r[t[0]]=t[1].trim()}return r}}export{NumberFn,StringFn,DateFn,FetchFn};